import reglogo from "./assets/reglogo.svg"
import reg from "./assets/reg.otf"
import screenlogo from "./assets/screenlogo.svg"
import screen from "./assets/screen.otf"

export default function mysketch(p) {

  let reg
  let screen
  let reglogo
  let screenlogo

  let bString, iString, oString, nString, cString

  p.preload = function(){
    reg = p.loadFont('/static/reg-27b93a163e1b08ebf79f909765b7582e.otf')
    reglogo = p.loadImage('data:image/svg+xml;base64,')
    screenlogo = p.loadImage('data:image/svg+xml;base64,')
    screen = p.loadFont('/static/screen-4ce1d0c4fa90b1429da5ec1e2b09c543.otf')
  }
  p.setup = function() {
    p.createCanvas(150, 44)
    p.frameRate(1)
    p.smooth()
  }

  p.getMarkovChar = function(arr) {
    let threshold = 0.8
    let passThreshold = p.random()
    if (passThreshold > threshold) {
      return (parseInt(p.random(1,arr.length)))
    } else {
      return 0
    }
  }
  p.getMarkovFont = function() {
    let threshold = 0.8
    let passThreshold = p.random()
    if(passThreshold > threshold) {
      return (p.textFont(screen))
    } else {
      return (p.textFont(reg))
    }
  }
  p.getMarkovLogo = function() {
      let threshold = 0.8
    let passThreshold = p.random()
    if(passThreshold > threshold) {
      p.image(screenlogo, 0, 20 - 18, 40, 40)
    } else {
      p.image(reglogo, 0, 20 - 18, 40, 40)
    }
    
  }

  p.draw = function() {
    p.background(255)
    p.fill(0)

    p.textSize(35)
    p.textAlign(p.CENTER, p.CENTER)

    p.getMarkovLogo()

    bString = ['B']
    iString = ['i', 'ï', 'í', 'ĭ', 'ì', 'î', 'ĩ', 'ī', 'į', 'y']
    oString = ['o','ò','ó','ô','õ','ö', 'ō','ō','ŏ','ő', 'ø']
    nString = ['n','ñ', 'ń', 'ņ', 'ň']
    cString = ['c','ç','ć','ĉ','ċ','č','k']

    let textHeight = p.height/2 - 2

    p.getMarkovFont()
    p.text(bString[0], 55, textHeight)

    p.getMarkovFont()
    p.text(iString[p.getMarkovChar(iString)], 70, textHeight)

    p.getMarkovFont()
    p.text(oString[p.getMarkovChar(oString)], 85, textHeight)

    p.getMarkovFont()
    p.text(nString[p.getMarkovChar(nString)], 105, textHeight)

    p.getMarkovFont()
    p.text(iString[p.getMarkovChar(iString)], 120, textHeight)

    p.getMarkovFont()
    p.text(cString[p.getMarkovChar(cString)], 135, textHeight)
  }
}
